export interface CurrencyRate {
}
export interface ListCurrenciesRequest {
}
export interface ListCurrenciesResponse {
    /** Supported currencies */
    currencies?: Currency[];
}
export interface Currency {
    /**
     * Currency code.
     * @format CURRENCY
     */
    code?: string;
    /**
     * Currency symbol.
     * @maxLength 10
     */
    symbol?: string;
}
export interface ConvertCurrencyRequest {
    /**
     * Amounts to convert.
     * @minSize 1
     * @maxSize 100
     */
    amounts?: DecimalValue[];
    /**
     * Original currency.
     * @format CURRENCY
     */
    from: string;
    /**
     * Target currency.
     * @format CURRENCY
     */
    to: string;
}
export interface DecimalValue {
    /** Value without decimals (e.g., for 10.95 value will be 1095). */
    value?: string;
    /** Decimal places to apply (e.g., for 10.95 decimal_places will be 2). */
    decimalPlaces?: number;
}
export interface ConvertCurrencyResponse {
    /** Converted amounts. */
    amounts?: DecimalValue[];
    /** Date and time the conversion rate was last updated. */
    rateTimestamp?: Date | null;
}
export interface ConversionRateRequest {
    /**
     * Original currency.
     * @format CURRENCY
     */
    from: string;
    /**
     * Target currency.
     * @format CURRENCY
     */
    to: string;
}
export interface ConversionRateResponse {
    /** Conversion rate between 2 currencies. */
    rate?: DecimalValue;
    /** Date and time the conversion rate was last updated. */
    rateTimestamp?: Date | null;
}
